import React from 'react'
import dummyProduct from '../../../data/dummyProduct'

export default function ItemDetails() {
    const breadcrumbs = "Ya veremos qué va aquí"
    const url = dummyProduct.item.picture;
    const condition = dummyProduct.item.condition;
    const itemsSold = dummyProduct.item.sold_quantity;
    const title = dummyProduct.item.title;
    const price = dummyProduct.item.price.amount;
    const description = dummyProduct.item.description;
    return (
        <div>
            <p>{breadcrumbs}</p>
            <div>
            <img src={url} alt=""/>
            <div>
                <p>{condition} - {itemsSold}</p>
                <h3>{title}</h3>
                <h1>{price}</h1>
                <button>Comprar</button>
            </div>
            </div>
            <div>
                <h2>
                    Descripción del Producto
                </h2>
                <p>{description}</p>
            </div>
        </div>
    )
}
