import React, { useState } from 'react'

const dummyProduct = {
    author: {
        name: "Diego M.",
        lastname: "Maldini Freyre"
    },
    item: {
        id: "MLA818555410",
        title: "Asrock X399 Taichi Str4 Sata 6gb/s Usb 3.1/3.0 Atx Amd Mo...",
        price: {
            currency: "ARS",
            amount: 88969,
            decimals: 0
        },
        picture: "http://http2.mlstatic.com/D_792801-MLA32449107349_102019-I.jpg",
        condition: "new",
        free_shipping: true,
        sold_quantity: 0,
        description: "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Laudantium repellat et molestias hic fugiat quisquam quos eaque deleniti odio quas a officia tempore modi nulla, quod, culpa saepe animi magnam? Expedita deserunt qui, quod recusandae debitis optio quas aut nam quo accusantium iure, amet quaerat totam dignissimos, ratione rerum commodi."
    }
}

export default function ItemDetails() {
    const [ product, setProduct ] = useState(dummyProduct);
    const breadcrumbs = "Ya veremos qué va aquí"
    const url = product.item.picture;
    const condition = product.item.condition;
    const itemsSold = product.item.sold_quantity;
    const title = product.item.title;
    const price = product.item.price.amount;
    const description = product.item.description;
    return (
        <div>
            <p>{breadcrumbs}</p>
            <div>
            <img src={url} alt=""/>
            <div>
                <p>{condition} - {itemsSold}</p>
                <h3>{title}</h3>
                <h1>{price}</h1>
                <button>Comprar</button>
            </div>
            </div>
            <div>
                <h2>
                    Descripción del Producto
                </h2>
                <p>{description}</p>
            </div>
        </div>
    )
}
